from api.use.mongodb_link import origindef senid2index(sentid):    #将形如"592436239afc10f2c9a240c8_12"的SENTID分解为ID和INDE，其中，INDEX转换为整数    res=sentid.split('_')    id=res[0]    index=int(res[1])    return id,indexdef find_origin(sentid):    #根据ID和INDEX查询ORIGIN库中的文章信息    res = sentid.split('_')    id = res[0]    index = int(res[1])    item=origin.find({'story_id':id})    if item.count()==0:        return {"msg":"no result"}    item=item[0]    ret={"msg":"success"}    ret['res']={}    ret['res']['title']=item['s_title']    ret['res']['url']=item['o_url']    ret['res']['sent']=item['s_parsed']['unparsed_sents'][int(index)].strip()    return ret#id,index=senid2index('5ae994a9aacf802249155fb3_0')#print(find_origin(id,index))